sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    //Type appropriate comment here, and begin script below
    var ci = g_form.getReference('cmdb_ci', callback);

    function callback(ci){
		var ciName = ci.name;
		if(ciName.match(/.*blackberry.*/i) || ciName.match(/.*iphone.*/i) || ciName.match(/.*android.*/i)){
			g_form.setValue('item_type','cmdb_ci_mobile_device','Mobile');
			g_form.setReadOnly('item_type',true);
		}else{
			g_form.setReadOnly('item_type',false);
		}
	}

}
